!function() {
    "use strict";
    function MyResourceService($q, $http, $timeout) {
        function get(id) {
            return self.list().then(function(resources) {
                var resource = resources[0];
                return angular.forEach(resources, function(resourceIter) {
                    id === resourceIter.id && (resource = resourceIter);
                }), resource;
            });
        }
        function list() {
            return $http.get("json/my-resources.json").then(function(resources) {
                return resources.data;
            });
        }
        function save(resource) {
            console.log("saving! " + resource.name);
            var deferred = $q.defer();
            return $timeout(function() {
                deferred.resolve({
                    data: resource
                });
            }, 2e3), deferred.promise;
        }
        var self = this;
        self.get = get, self.list = list, self.save = save;
    }
    angular.module("my-application.services").service("MyResourceService", MyResourceService), 
    MyResourceService.$inject = [ "$q", "$http", "$timeout" ];
}();